function readShow(){let pKey=$("#password").val();let pKeysha224=sha224(pKey);let cKeysha224=$("meta#sha224").attr("sha224");if(pKeysha224==cKeysha224){$.cookie("pKey",pKey,{path:"/"});console.log($.cookie("pKey"));$("div#article-container.post-content").html(decrypt(pKey,$("div#article-container.post-content").text()));$("input#password").hide();$("button#button").hide();$("div#article-container.post-content").show()}else{alert("您输入的密码错误，请重试")}}$(document).ready(function(){$("div#article-container.post-content").hide();$("input#password").hide();$("button#button").hide();if($.cookie("pKey")===undefined){$("input#password").show();$("button#button").show();$("#button").click(function(){readShow()});$("input#password").bind("keypress",function(a){if(a.keyCode=="13"){readShow()}})}else{let pKey=$.cookie("pKey");$("div#article-container.post-content").html(decrypt(pKey,$("div#article-container.post-content").text()));$("div#article-container.post-content").show()}});
